import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import Card from '../../components/Card';
import Button from '../../components/Button';
import { QrReader } from 'react-qr-reader';

const HospitalQRScannerPage: React.FC = () => {
  const [scanResult, setScanResult] = useState<string | null>(null);
  const [userInfo, setUserInfo] = useState<any>(null);
  const [scanHistory, setScanHistory] = useState<string[]>([]);
  const [isScanning, setIsScanning] = useState(false);

  const startScanning = () => {
    setIsScanning(true);
    setScanResult(null);
    setUserInfo(null);
  };

  const stopScanning = () => {
    setIsScanning(false);
  };

  const handleScan = async (data: string | null) => {
    if (data) {
      try {
        const parsed = JSON.parse(data);
        setScanResult(data);
        setIsScanning(false);

        if (parsed.userId) {
          const token = localStorage.getItem('token');
          if (!token) throw new Error('Authentication token missing');

          const response = await fetch(`http://localhost:5000/api/user/${parsed.userId}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          if (!response.ok) throw new Error('Failed to fetch user data');

          const userData = await response.json();
          setUserInfo(userData);

          // Add to scan history
          setScanHistory(prev => [data, ...prev.slice(0, 4)]);
        }
      } catch (err) {
        console.error('Invalid QR code data', err);
        alert('Scanned QR code data is invalid or corrupted.');
        setScanResult(null);
        setUserInfo(null);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }} className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">QR Code Scanner</h1>
              <p className="text-gray-600 mt-2">Scan patient QR codes to process payments and fund transfers.</p>
            </div>
            <Link to="/hospital/dashboard">
              <Button variant="outline">← Back to Dashboard</Button>
            </Link>
          </div>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Scanner Section */}
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5, delay: 0.1 }}>
            <Card>
              <h2 className="text-xl font-semibold text-gray-900 mb-6">QR Code Scanner</h2>

              {isScanning ? (
                <>
                  <QrReader
                    facingMode="environment"
                    delay={300}
                    onScan={handleScan}
                    onError={(err) => console.error('QR scanner error:', err)}
                    style={{ width: '100%' }}
                  />
                  <div className="mt-6 flex space-x-3">
                    <Button variant="danger" onClick={stopScanning} className="flex-1">
                      Stop Scanning
                    </Button>
                  </div>
                </>
              ) : (
                <div className="text-center py-20 bg-gray-900 rounded-lg text-white">
                  {!scanResult ? (
                    <>
                      <p className="mb-4">Ready to scan</p>
                      <Button onClick={startScanning} className="flex-1">
                        Start Scanning
                      </Button>
                    </>
                  ) : (
                    <>
                      <p className="mb-4">Scanned Data:</p>
                      <pre className="whitespace-pre-wrap bg-gray-800 p-4 rounded">{scanResult}</pre>
                      <Button onClick={() => setScanResult(null)} className="mt-4">
                        Clear Result
                      </Button>
                      <Button onClick={startScanning} className="mt-4 ml-3">
                        Scan Again
                      </Button>
                    </>
                  )}
                </div>
              )}

              {userInfo && (
                <div className="mt-6 p-4 border border-gray-300 rounded-lg bg-white">
                  <h3 className="text-lg font-semibold mb-2">User Information</h3>
                  <p><b>Name:</b> {userInfo.name}</p>
                  <p><b>Email:</b> {userInfo.email}</p>
                  {/* Add more fields as needed */}
                </div>
              )}
            </Card>
          </motion.div>

          {/* Scan History */}
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5, delay: 0.2 }}>
            <Card>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Scans</h3>
              {scanHistory.length > 0 ? (
                <div className="space-y-3">
                  {scanHistory.map((scan, index) => (
                    <div key={index} className="flex items-center justify-between py-3 border-b border-gray-100 last:border-b-0">
                      <pre className="text-sm whitespace-pre-wrap">{scan}</pre>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <p>No recent scans</p>
                </div>
              )}
            </Card>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default HospitalQRScannerPage;
